import React from "react";
import styled, { ThemeProvider } from "styled-components";
import { Transition } from "arclight-react";

const Constraints = styled.div`
  position: relative;
  padding-top: 142.25%;
  width: 100%;
  overflow: hidden;
  font-size: 1.5vw;
`;

const Card = styled.div`
  & img {
    opacity: 1;
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
  }
`;

const Sub = styled.div`
  & img {
    height: 98%;
    -webkit-mask-image: -webkit-gradient(
      linear,
      left top,
      left bottom,
      from(rgba(0, 0, 0, 1)),
      to(rgba(0, 0, 0, 0))
    );
    mask-image: linear-gradient(to bottom, rgba(0, 0, 0, 1), rgba(0, 0, 0, 0));
  }
`;

const Text = styled.div`
  position: absolute;
  bottom: 5%;
  right: 7.5%;
  font-weight: bold;
  font-style: italic;
  color: white;
`;

const BackDrop = styled.div`
  position: absolute;
  top: 0;
  width: 100%;
  height: 98%;
  background-color: #141414;
`;

export default class _ extends React.Component {
  state = { revealed: false };

  render() {
    return (
      <ThemeProvider theme={{}}>
        <Transition
          trans={{
            animation: this.props.loadAnims.anims[0],
            delay: this.props.loadAnims.delay,
          }}
        >
          <Transition
            trans={{
              animation: this.props.loadAnims.anims[1],
              delay: this.props.loadAnims.delay + 1,
            }}
          >
            <Constraints>
              <Card>
                <BackDrop />
                <Sub>
                  <img
                    src={
                      this.state.revealed
                        ? require("./img/yone.jpg")
                        : this.state.team === "team_1"
                        ? this.props.cardBack
                        : require("../../../config/assets/red.png")
                    }
                  />
                </Sub>
                <img
                  src={
                    this.state.team === "team_1"
                      ? require("../../../config/assets/border-blue.png")
                      : require("../../../config/assets/border-red.png")
                  }
                />
              </Card>
              {this.state.revealed ? <Text>Yone</Text> : null}
            </Constraints>
          </Transition>
        </Transition>
      </ThemeProvider>
    );
  }
}
