import React from "react";
import styled, { ThemeProvider } from "styled-components";
import { Timer, Transition } from "arclight-react";

const TIMER_SIZE = 100;

const Timer1 = styled.div`
  position: absolute;
  top: 35%;
  left: ${TIMER_SIZE / 2}px
  transform: translateY(-35%);
`;

const Timer2 = styled.div`
  position: absolute;
  top: 35%;
  right: ${TIMER_SIZE / 2}px
  transform: translateY(-35%);
`;

export default class _ extends React.Component {
  render() {
    return (
      <ThemeProvider theme={{}}>
        <Timer1>
          <Transition
            trans={
              this.props.STATE.team_active === 1
                ? this.props.STATE.config.TIMER.LEFT_TRANSTION
                : this.props.STATE.config.TIMER.LEFT_TRANSTION_OUT
            }
          >
            <Timer
              theme={this.props.STATE.config.THEME}
              isPlaying={false}
              size={TIMER_SIZE}
              seconds={60}
              crit={7}
              strokeWidth={4}
              onComplete={() => console.log("Timer Finished")}
            />
          </Transition>
        </Timer1>

        <Transition
          trans={
            this.props.STATE.team_active === 2
              ? this.props.STATE.config.TIMER.RIGHT_TRANSTION
              : this.props.STATE.config.TIMER.RIGHT_TRANSTION_OUT
          }
        >
          <Timer2>
            <Timer
              theme={this.props.STATE.config.THEME}
              isPlaying={false}
              size={TIMER_SIZE}
              seconds={60}
              crit={7}
              strokeWidth={4}
              onComplete={() => console.log("Timer Finished")}
            />
          </Timer2>
        </Transition>
      </ThemeProvider>
    );
  }
}
