import React from "react";
import styled, { ThemeProvider } from "styled-components";
import { Timer, Transition } from "arclight-react";

const TIMER_SIZE = 100;

const _Timer = styled.div`
  position: absolute;
  top: 35%;
  ${(props) =>
    props.team_active === 1
      ? `left: ${TIMER_SIZE / 2}px`
      : `right: ${TIMER_SIZE / 2}px`};
  transform: translateY(-35%);
`;

export default class _ extends React.Component {
  grabTimerTrans() {
    switch (this.props.STATE.team_active) {
      case 0:
        return this.props.STATE.last_team_active === 1
          ? this.props.STATE.config.TIMER.LEFT_TRANSTION_OUT
          : this.props.STATE.config.TIMER.RIGHT_TRANSTION_OUT;
      case 1:
        return this.props.STATE.config.TIMER.LEFT_TRANSTION;
      case 2:
        return this.props.STATE.config.TIMER.RIGHT_TRANSTION;
    }
  }

  render() {
    return (
      <ThemeProvider theme={{}}>
        <_Timer team_active={this.props.STATE.team_active}>
          <Transition trans={this.grabTimerTrans()}>
            <Timer
              theme={this.props.STATE.config.THEME}
              isPlaying={false}
              size={TIMER_SIZE}
              seconds={60}
              crit={7}
              strokeWidth={4}
              onComplete={() => console.log("Timer Finished")}
            />
          </Transition>
        </_Timer>
      </ThemeProvider>
    );
  }
}
