import React from "react";
import styled, { ThemeProvider } from "styled-components";
import { Grid, Button, Transition } from "arclight-react";
import Card from "../Card/Card";
import Ban from "../Ban/Ban";

const _Grid = styled.div`
  margin: 25px;
  position: absolute;
  top: 60%;
  transform: translateY(-60%);
`;

export default class _ extends React.Component {
  render() {
    return (
      <ThemeProvider theme={{}}>
        <_Grid>
          <table width={"100%"}>
            <tbody>
              <tr>
                <td>
                  <Grid
                    theme={"Dark"}
                    itemsPerRow={5}
                    items={[
                      <Card
                        cardBack={this.props.STATE.config.TEAM_1.CARD_BACK}
                        border={this.props.STATE.config.TEAM_1.CARD_BORDER}
                        loadAnims={{
                          anims: ["fadeInLeft", "flip"],
                          delay: 1,
                        }}
                      />,
                      <Card
                        cardBack={this.props.STATE.config.TEAM_1.CARD_BACK}
                        border={this.props.STATE.config.TEAM_1.CARD_BORDER}
                        loadAnims={{
                          anims: ["fadeInLeft", "flip"],
                          delay: 1.25,
                        }}
                      />,
                      <Card
                        cardBack={this.props.STATE.config.TEAM_1.CARD_BACK}
                        border={this.props.STATE.config.TEAM_1.CARD_BORDER}
                        loadAnims={{
                          anims: ["fadeInLeft", "flip"],
                          delay: 1.5,
                        }}
                      />,
                      <Card
                        cardBack={this.props.STATE.config.TEAM_1.CARD_BACK}
                        border={this.props.STATE.config.TEAM_1.CARD_BORDER}
                        loadAnims={{
                          anims: ["fadeInLeft", "flip"],
                          delay: 1.75,
                        }}
                      />,
                      <Card
                        cardBack={this.props.STATE.config.TEAM_1.CARD_BACK}
                        border={this.props.STATE.config.TEAM_1.CARD_BORDER}
                        loadAnims={{
                          anims: ["fadeInLeft", "flip"],
                          delay: 2,
                        }}
                      />,
                    ]}
                  />
                </td>
                <td width={"5%"} />
                <td>
                  <Grid
                    theme={"Dark"}
                    itemsPerRow={5}
                    items={[
                      <Card
                        cardBack={this.props.STATE.config.TEAM_2.CARD_BACK}
                        border={this.props.STATE.config.TEAM_2.CARD_BORDER}
                        loadAnims={{
                          anims: ["fadeInRight", "flip"],
                          delay: 2,
                        }}
                      />,
                      <Card
                        cardBack={this.props.STATE.config.TEAM_2.CARD_BACK}
                        border={this.props.STATE.config.TEAM_2.CARD_BORDER}
                        loadAnims={{
                          anims: ["fadeInRight", "flip"],
                          delay: 1.75,
                        }}
                      />,
                      <Card
                        cardBack={this.props.STATE.config.TEAM_2.CARD_BACK}
                        border={this.props.STATE.config.TEAM_2.CARD_BORDER}
                        loadAnims={{
                          anims: ["fadeInRight", "flip"],
                          delay: 1.5,
                        }}
                      />,
                      <Card
                        cardBack={this.props.STATE.config.TEAM_2.CARD_BACK}
                        border={this.props.STATE.config.TEAM_2.CARD_BORDER}
                        loadAnims={{
                          anims: ["fadeInRight", "flip"],
                          delay: 1.25,
                        }}
                      />,
                      <Card
                        cardBack={this.props.STATE.config.TEAM_2.CARD_BACK}
                        border={this.props.STATE.config.TEAM_2.CARD_BORDER}
                        loadAnims={{
                          anims: ["fadeInRight", "flip"],
                          delay: 1,
                        }}
                      />,
                    ]}
                  />
                </td>
              </tr>
            </tbody>
          </table>
          <table width={"100%"}>
            <tbody>
              <tr>
                <td>
                  <Grid
                    theme={"Dark"}
                    itemsPerRow={5}
                    items={[
                      <Card
                        cardBack={this.props.STATE.config.TEAM_1.CARD_BACK}
                        border={this.props.STATE.config.TEAM_1.CARD_BORDER}
                        loadAnims={{
                          anims: ["fadeInLeft", "flip"],
                          delay: 1,
                        }}
                      />,
                      <Card
                        cardBack={this.props.STATE.config.TEAM_1.CARD_BACK}
                        border={this.props.STATE.config.TEAM_1.CARD_BORDER}
                        loadAnims={{
                          anims: ["fadeInLeft", "flip"],
                          delay: 1.25,
                        }}
                      />,
                      <Card
                        cardBack={this.props.STATE.config.TEAM_1.CARD_BACK}
                        border={this.props.STATE.config.TEAM_1.CARD_BORDER}
                        loadAnims={{
                          anims: ["fadeInLeft", "flip"],
                          delay: 1.5,
                        }}
                      />,
                      <Card
                        cardBack={this.props.STATE.config.TEAM_1.CARD_BACK}
                        border={this.props.STATE.config.TEAM_1.CARD_BORDER}
                        loadAnims={{
                          anims: ["fadeInLeft", "flip"],
                          delay: 1.75,
                        }}
                      />,
                      <Card
                        cardBack={this.props.STATE.config.TEAM_1.CARD_BACK}
                        border={this.props.STATE.config.TEAM_1.CARD_BORDER}
                        loadAnims={{
                          anims: ["fadeInLeft", "flip"],
                          delay: 2,
                        }}
                      />,
                    ]}
                  />
                </td>
                <td width={"35%"} align={"center"}>
                  <Transition trans={{ animation: "pulse", count: "infinite" }}>
                    <Button
                      trans={{
                        animation:
                          this.props.STATE.team_active > 0
                            ? "fadeIn"
                            : "fadeOut",
                      }}
                      theme={"Light"}
                      pop
                      onClick={() =>
                        this.props.STATE.team_active > 0
                          ? this.props.STATE.setModalStatus(true)
                          : null
                      }
                    >
                      Champion Select
                    </Button>
                  </Transition>
                </td>
                <td>
                  <Grid
                    theme={"Dark"}
                    itemsPerRow={5}
                    items={[
                      <Card
                        cardBack={this.props.STATE.config.TEAM_2.CARD_BACK}
                        border={this.props.STATE.config.TEAM_2.CARD_BORDER}
                        loadAnims={{
                          anims: ["fadeInRight", "flip"],
                          delay: 2,
                        }}
                      />,
                      <Card
                        cardBack={this.props.STATE.config.TEAM_2.CARD_BACK}
                        border={this.props.STATE.config.TEAM_2.CARD_BORDER}
                        loadAnims={{
                          anims: ["fadeInRight", "flip"],
                          delay: 1.75,
                        }}
                      />,
                      <Card
                        cardBack={this.props.STATE.config.TEAM_2.CARD_BACK}
                        border={this.props.STATE.config.TEAM_2.CARD_BORDER}
                        loadAnims={{
                          anims: ["fadeInRight", "flip"],
                          delay: 1.5,
                        }}
                      />,
                      <Card
                        cardBack={this.props.STATE.config.TEAM_2.CARD_BACK}
                        border={this.props.STATE.config.TEAM_2.CARD_BORDER}
                        loadAnims={{
                          anims: ["fadeInRight", "flip"],
                          delay: 1.25,
                        }}
                      />,
                      <Card
                        cardBack={this.props.STATE.config.TEAM_2.CARD_BACK}
                        border={this.props.STATE.config.TEAM_2.CARD_BORDER}
                        loadAnims={{
                          anims: ["fadeInRight", "flip"],
                          delay: 1,
                        }}
                      />,
                    ]}
                  />
                </td>
              </tr>
            </tbody>
          </table>
        </_Grid>
      </ThemeProvider>
    );
  }
}
