import React from "react";
import styled from "styled-components";
import { Img, Grid, Timer, Button, Transition } from "arclight-react";

const Select = styled.div`
  width: inherit;
  height: inherit;
`;

const _Title = styled.div`
  color: #dfdfdf;
  margin-top: 15px;
  & table {
    border-spacing: 20px 0;
  }
`;

const Container = styled.div`
  height: calc(100% - 33.5px);
  ${(props) => (props.confirming ? "pointer-events: none;" : null)}
`;

const Prompt = styled.div`
  ${(props) => (!props.confirming ? "pointer-events: none;" : null)}
  color: #cdcdcd;
  background-color: rgba(0, 0, 0, 0.95);
  border-radius: 4px;
  padding: 15px;
  position: absolute;
  top: 75%;
  left: 50%;
  transform: translate(-50%, -75%);
  .group {
    display: inline-flex;
    & .bttn {
      margin-left: 10px;
      margin-right: 10px;
    }
  }
`;

const Item = styled.div`
  ${(props) =>
    props.disabled || props.banned || props.picked
      ? "pointer-events: none;"
      : null}
  ${(props) =>
    props.disabled || props.banned || props.picked
      ? "opacity: 0.3;"
      : "opacity: 1;"}
  position: relative;
  width: 100%;
  color: #e0e0e0;
  cursor: pointer;
  & img {
    width: 100%;
    border-radius: 3px;
    cursor: pointer;
  }
  &:hover {
    opacity: 0.3;
  }
  .sub {
    position: absolute;
    width: 100%;
    height: 75px;
    bottom: 0;
    right: 0;
    background-image: linear-gradient(to top, #000000, rgba(255, 255, 255, 0));
    border-radius: 3px;
    text-align: center;
    .text {
      margin: 3px;
      position: absolute;
      bottom: 0;
      width: 100%;
    }
  }
  .roles {
    position: absolute;
    top: 0;
    left: 0;
    text-align: left;
    margin: 2px;
    & img {
      width: 20%;
      border-radius: 50%;
      margin: 0px;
    }
  }
`;

export default class _ extends React.Component {
  state = {
    charactersArray: null,
    confirming: false,
  };

  componentDidMount() {
    this.fill();
  }

  fill() {
    const charactersArray = [];
    for (let i = 0; i < 150; i++) {
      charactersArray.push(
        <Item onClick={() => this.setState({ confirming: true })}>
          <Img src={require("../img/yone_square.jpg")} />
          <div className={"roles"}>
            <Img src={this.props.STATE.config.MODAL.SEARCH.FIGHTER} />
            <Img src={this.props.STATE.config.MODAL.SEARCH.MAGE} />
          </div>
          <div className={"sub"}>
            <div className={"text"}>Yone</div>
          </div>
        </Item>
      );
    }
    this.setState({ charactersArray });
  }

  render() {
    return (
      <Select>
        <_Title>
          <table>
            <tbody>
              <tr>
                <td>
                  <Timer
                    theme={this.props.STATE.config.THEME}
                    isPlaying={false}
                    size={75}
                    seconds={60}
                    crit={7}
                    strokeWidth={3}
                    onComplete={() => console.log("Timer Finished")}
                  />
                </td>
                <td>
                  <h1>
                    <b>
                      <i>Ban Phase</i>
                    </b>
                  </h1>
                </td>
              </tr>
            </tbody>
          </table>
        </_Title>
        <Container confirming={this.state.confirming}>
          {this.state.charactersArray ? (
            <Grid
              theme={"Dark"}
              search={true}
              itemsPerRow={8}
              height={"100%"}
              filtersRight
              filters={[
                {
                  display: (
                    <Img src={this.props.STATE.config.MODAL.SEARCH.FIGHTER} />
                  ),
                  active: true,
                  onClick: (filter) => console.log(filter.active),
                },
                {
                  display: (
                    <Img src={this.props.STATE.config.MODAL.SEARCH.MAGE} />
                  ),
                  active: true,
                  onClick: (filter) => console.log(filter.active),
                },
                {
                  display: (
                    <Img src={this.props.STATE.config.MODAL.SEARCH.MARKSMAN} />
                  ),
                  active: true,
                  onClick: (filter) => console.log(filter.active),
                },
                {
                  display: (
                    <Img src={this.props.STATE.config.MODAL.SEARCH.ASSASSIN} />
                  ),
                  active: true,
                  onClick: (filter) => console.log(filter.active),
                },
                {
                  display: (
                    <Img src={this.props.STATE.config.MODAL.SEARCH.TANK} />
                  ),
                  active: true,
                  onClick: (filter) => console.log(filter.active),
                },
                {
                  display: (
                    <Img src={this.props.STATE.config.MODAL.SEARCH.SUPPORT} />
                  ),
                  active: true,
                  onClick: (filter) => console.log(filter.active),
                },
              ]}
              items={this.state.charactersArray}
            />
          ) : null}
        </Container>
        <Transition
          trans={{
            animation: this.state.confirming ? "fadeIn" : "fadeOut",
            duration: 0.35,
          }}
        >
          <Prompt confirming={this.state.confirming}>
            <h3>Select Yone?</h3>
            <div className={"group"}>
              <div className={"bttn"}>
                <Button theme={"Light"} pop onClick={() => console.log("test")}>
                  Confirm
                </Button>
              </div>
              <div className={"bttn"}>
                <Button
                  theme={"Light"}
                  pop
                  onClick={() => this.setState({ confirming: false })}
                >
                  Cancel
                </Button>
              </div>
            </div>
          </Prompt>
        </Transition>
      </Select>
    );
  }
}
