import React from "react";
import styled, { ThemeProvider } from "styled-components";
import { MediaCycler, Particles } from "arclight-react";

const Background = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  overflow: hidden;
  opacity: 0.3;
`;

const _Particles = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
`;

export default class _ extends React.Component {
  render() {
    return (
      <ThemeProvider theme={{}}>
        <Background>
          <_Particles>
            <Particles
              width={"1000px"}
              height={"1000px"}
              animations={[
                {
                  name: "spiritual",
                  particleColor: "#9600ff",
                  x: 50,
                  y: 50,
                },
              ]}
              invoke={false}
            />
          </_Particles>
          <MediaCycler
            randomize={true}
            trans={null}
            width={"100%"}
            type={"video"}
            elements={[
              require("./img/ionia.webm"),
              require("./img/targon.webm"),
              require("./img/wukong.mp4"),
            ]}
            interval={15}
            elementTransition={{
              transIn: { animation: "fadeIn", duration: 3 },
              transOut: { animation: "fadeOut", duration: 3 },
            }}
          />
        </Background>
      </ThemeProvider>
    );
  }
}
