import React from "react";
import styled, { ThemeProvider } from "styled-components";
import { Transition } from "arclight-react";

const Constraints = styled.div`
  width: 100%;
  height: ${(props) => props.width * 1.4133333333333333}px;
  top: 50%;
  left: 50%;
  overflow: hidden;
`;

const PickCard = styled.div`
  position: relative;
  & img {
    opacity: 1;
    position: absolute;
    width: 100%;
    top: 0;
    left: 0;
  }
`;

export default class _ extends React.Component {
  constructor(props) {
    super(props);
    this.contRef = React.createRef();
  }

  componentDidMount() {
    console.log(this.contRef.current.clientWidth);
  }

  render() {
    return (
      <ThemeProvider theme={{}}>
        <Transition>
          <Constraints
            ref={this.contRef}
            width={this.contRef.current.clientWidth}
          >
            <PickCard>
              <img src={require("./img/yone.jpg")} />
              <img src={require("../../../config/assets/border.png")} />
            </PickCard>
          </Constraints>
        </Transition>
      </ThemeProvider>
    );
  }
}
