import React from "react";
import styled, { ThemeProvider } from "styled-components";
import { MediaCycler, Particles } from "arclight-react";

const Background = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  overflow: hidden;
  opacity: 0.3;
`;

const Tint = styled.div`
  ${(props) =>
    `background-image: linear-gradient(to ${
      props.team_active === 1 ? "left" : "right"
    }, ${
      props.team_active === 1 ? props.team_1_tint : props.team_2_tint
    }, rgba(255, 255, 255, 0))`};
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
`;

export default class _ extends React.Component {
  render() {
    return (
      <ThemeProvider theme={{}}>
        <Background>
          <MediaCycler
            randomize={true}
            trans={null}
            width={"100%"}
            type={"video"}
            elements={[
              require("./img/ionia.webm"),
              require("./img/targon.webm"),
              require("./img/wukong.mp4"),
            ]}
            interval={15}
            elementTransition={{
              transIn: { animation: "fadeIn", duration: 3 },
              transOut: { animation: "fadeOut", duration: 3 },
            }}
          />
          <Tint
            team_active={this.props.STATE.team_active}
            team_1_tint={this.props.STATE.config.BACKGROUND.TEAM1TINT}
            team_2_tint={this.props.STATE.config.BACKGROUND.TEAM2TINT}
          />
        </Background>
      </ThemeProvider>
    );
  }
}
